import * as React from 'react';
import { Platform, StyleSheet, View } from 'react-native';

const FAR_FAR_AWAY = 3000; // this should be big enough to move the whole view out of its container

export default class ResourceSavingScene extends React.Component {
  render() {
    const { isVisible, children, style, ...rest } = this.props;

    return <View style={[styles.container, style]} collapsable={false} removeClippedSubviews={
    // On iOS, set removeClippedSubviews to true only when not focused
    // This is an workaround for a bug where the clipped view never re-appears
    Platform.OS === 'ios' ? !isVisible : true} pointerEvents={isVisible ? 'auto' : 'none'} {...rest}>
        <View style={isVisible ? styles.attached : styles.detached}>
          {children}
        </View>
      </View>;
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    overflow: 'hidden'
  },
  attached: {
    flex: 1
  },
  detached: {
    flex: 1,
    top: FAR_FAR_AWAY
  }
});
